import styles from "./TipUI.module.css";
import dollar from "./icon-dollar.svg";
import person from "./icon-person.svg";
import { useState, useEffect } from "react";
const UserInput = () => {
    const [Bill, setBill] = useState(0)
    const [Tip, setTip] = useState(0)
    const [NOP, setNOP] = useState(0)
    const [isValid, SetIsValid] = useState(false)
    const [Data, setData] = useState({})
    let Valid = {
        Bill: false,
        Tip: false,
        NOP: NOP
    }
    const onBill = (e) => {
        setBill(e.target.value)
        Valid.Bill = true
    }
    const onTip = (e) => {
        setTip(parseInt(e.target.innerText))
    }
    const onTipCustom = (e) => {
        setTip(e.target.value)
    }
    const onNOP = (e) => {
        setNOP(e.target.value)
    }
    const onValid = () => {

    }
    useEffect(() => {
        setData({
            Bill: Bill,
            Tip: Tip,
            NOP: NOP
        })
    }, [isValid])
  return (
    <div className={styles.userInput}>
      <div>
        <p>Bill</p>
        <div className={styles.billContiner}>
          <img src={dollar} alt="dollar" />
          <input type="number" placeholder="0.0" onChange={onBill}/>
        </div>
      </div>
      <div>
        <p>Select Tip %</p>
        <div className={styles.TipBtn}>
          <button onClick={onTip}>5%</button>
          <button onClick={onTip}>10%</button>
          <button onClick={onTip}>15%</button>
          <button onClick={onTip}>25%</button>
          <button onClick={onTip}>50%</button>
          <input type="number" placeholder="Custom" onChange={onTipCustom} />
        </div>
      </div>
      <div>
        <p>Number of People</p>
        <div className={styles.billContiner}>
          <img src={person} alt="person" />
          <input type="number" placeholder="5" onChange={onNOP}/>
        </div>
      </div>
    </div>
  );
};
export default UserInput
