import { useState } from "react";
import styles from "./TipUI.module.css";
import logo from "./logo.svg";
import UserInput from "./UserInput";
import UserResult from "./UserResult";
const TipUI = () => {
  const [Reset, setReset] = useState({ Bill: 0, Tip: 0, NOP: 0 });
  const DUMMY_DATA = {
    TipAmount: 0.0,
    Total: 0.0,
  };
  const onReset = (e) => {
    setReset(e);
  };
  const [saveData, SetSavaData] = useState(DUMMY_DATA);
  const getData = (Data) => {
    SetSavaData(Data);
  };
  console.log(saveData);
  return (
    <div className={styles.main}>
      <img src={logo} alt="logo" />
      <div className={styles.continer}>
        <UserInput getData={getData} Reset={Reset} />
        <UserResult saveData={saveData} onReset={onReset} />
      </div>
    </div>
  );
};

export default TipUI;
